mapper.insert:
INSERT INTO board
SET title = #{title}
, contents = #{contents}
, user_id = #{userId}

mapper.select:
SELECT b.id, b.title, b.created_at, u.nm FROM board b JOIN user u ON b.user_id = u.id
WHERE b.title LIKE CONCAT('%', #{searchText}, '%') OR b.contents LIKE CONCAT('%', #{searchText}, '%')
ORDER BY b.id DESC LIMIT #{startIdx}, #{size}

mapper.update:
UPDATE board
SET title = #{title}
, contents = #{contents}
WHERE id = #{id}
AND user_id = #{userId}

mapper.delete:
DELETE FROM board
WHERE id = #{id}
AND user_id = #{signedUserId}
